sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...
try {
    var newMembershipObj = [];
    while (inputs.groupMembers.next()){
        newMembershipObj.push(inputs.groupMembers.getUniqueValue());
    }
        if(!gs.nil(newMembershipObj)){
                var newGrpMemebersList = newMembershipObj.toString().split(",");
            var grpMembers = new GlideRecord('sys_user_grmember');
            grpMembers.addQuery('group', inputs.assignmentGroupID);
            grpMembers.query();
            while (grpMembers.next()) {
                var usrValue = grpMembers.getValue('user');
                var index = newGrpMemebersList.indexOf(usrValue);
                if (index >= 0) {
                    newGrpMemebersList.splice(index, 1);
                } else {
                    grpMembers.deleteRecord(); //delete unmatched member
                }
            }
            //code for add the new group members which are not part in old group
            if (newGrpMemebersList.length > 0) {
                var grmem = new GlideRecord('sys_user_grmember');
                var usr = new GlideRecord('sys_user');
                usr.addQuery("sys_id", "IN", newGrpMemebersList.join() + "");
                usr.query();
                while (usr.next()) {
                    grmem.initialize();
                    grmem.group = inputs.assignmentGroupID;
                    grmem.user = usr.sys_id;
                    grmem.insert();
                }
            }
            }            
        } catch (e) {
            gs.info('updateGroupMembers had Error ' + e);
        }

})(inputs, outputs);

