sys_variable_value.value:
(function execute(inputs, outputs) {
    // ... code ...

    var approvalGroups = inputs.approval_groups;
    var approvalRules = '';
    var approvalGroupsSysIDs = [];

    while (approvalGroups.next()) {
        var grpRecSysID = approvalGroups.getUniqueValue();
        approvalGroupsSysIDs.push(grpRecSysID);

        if (gs.nil(approvalRules))
            approvalRules = 'ApprovesAnyG[' + grpRecSysID + ']';
        else
            approvalRules = approvalRules + '&ApprovesAnyG[' + grpRecSysID + ']';
    }

    approvalRules = approvalRules + 'OrRejectsAnyG[' + approvalGroupsSysIDs + ']';
    outputs.approval_rules = approvalRules;

})(inputs, outputs);

